import React from "react";
import "./ArtistCard.css";

shuffleArtists = artists => {
    let i = artists.length - 1;
    while (i > 0) {
      const j = Math.floor(Math.random() * (i + 1));
      const temp = artists[i];
      artists[i] = artists[j];
      artists[j] = temp;
      i--;
    }
    return artists;
  };


  handleCorrectGuess = newArtist => {
    const { topScore, score } = this.state;
    const newScore = score + 1;
    const newTopScore = Math.max(newScore, topScore);

    this.setState({
      artist: this.shuffleArtists(newArtist),
      score: newScore,
      topScore: newTopScore
    });
  };


const ArtistCard = props => (
    <div className="card">
        <div className="img-container">
            <a onClick={() => props.handleCorrectGuess(props.artist)}
                className={props.score === 0 ? "style_prevu_kit style_prevu_kit_ex" : "style_prevu_kit"}
                >
                    <img alt={props.artist} src={props.image} />
                </a>

        </div>
    </div>
);

export default ArtistCard;
